package constant

const (
	// WarnColor is the color for warning messages
	WarnColor = "\x1b[33m"

	// ErrorColor is the color for error messages
	ErrorColor = "\x1b[31m"

	// InfoColor is the color for info messages
	InfoColor = "\x1b[32m"

	// DebugColor is the color for debug messages
	DebugColor = "\x1b[34m"

	// ResetColor is the color for resetting the terminal
	ResetColor = "\x1b[0m"
)
